mapper.select:
select

from t_okr_key_result
where t_okr_key_result.objective_id = #{objectiveId}
  and t_okr_key_result.deleted_flag = #{deletedFlag}
order by t_okr_key_result.sort asc, t_okr_key_result.key_result_id desc

mapper.update:
update t_okr_key_result
set deleted_flag = #{deletedFlag}
where objective_id = #{objectiveId}

update t_okr_key_result
set deleted_flag = #{deletedFlag}
where key_result_id = #{keyResultId}
